mapper.select:
		select * from tbl_employee

		
			
				id = #{id}
			
			
				and last_name like #{lastName}
			
			
				email = #{email}
			
			
				gender = #{gender}

mapper.update:
		update tbl_employee 		
				
			
				last_name = #{lastName},
			
			
				email = #{email},
			
			
				gender = #{gender}
			
		
		where id = #{id}
